package esa.s1pdgs.cpoc.scaler.config;

import static org.junit.Assert.assertEquals;

import org.junit.Test;
import org.junit.runner.RunWith;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.test.annotation.DirtiesContext;
import org.springframework.test.context.junit4.SpringRunner;

import esa.s1pdgs.cpoc.scaler.config.K8SProperties;

@RunWith(SpringRunner.class)
@SpringBootTest
@DirtiesContext
public class K8SPropertiesTest {

	//@Rule
	//public KafkaEmbedded embeddedKafka = new KafkaEmbedded(1, true, "t-pdgs-aio-execution-jobs");
	
	@Autowired
	private K8SProperties k8sProperties;
	
	@Test
	public void k8sPropertiesTest() {
		assertEquals("Invalid master url", "https://1.1.4.5:6443", k8sProperties.getMasterUrl());
		assertEquals("Invalid namespace", "default", k8sProperties.getNamespace());
		assertEquals("Invalid username", "kubernetes-admin", k8sProperties.getUsername());
		assertEquals("Invalid client key", "", k8sProperties.getClientKey());
		assertEquals("Invalid client cert data", "", k8sProperties.getClientCertData());
	}

}
